	/**
	 * Retrieve object using using composite pkey values.
	 * @param      int $featurevectortype_id
	 * @param      int $track_id
	 * @param      int $distancetype_id
	 * @param      PropelPDO $con
	 * @return     Distancejob
	 */
	public static function retrieveByPK($featurevectortype_id, $track_id, $distancetype_id, PropelPDO $con = null) {
		$key = serialize(array((string) $featurevectortype_id, (string) $track_id, (string) $distancetype_id));
 		if (null !== ($obj = DistancejobPeer::getInstanceFromPool($key))) {
 			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(DistancejobPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}
		$criteria = new Criteria(DistancejobPeer::DATABASE_NAME);
		$criteria->add(DistancejobPeer::FEATUREVECTORTYPE_ID, $featurevectortype_id);
		$criteria->add(DistancejobPeer::TRACK_ID, $track_id);
		$criteria->add(DistancejobPeer::DISTANCETYPE_ID, $distancetype_id);
		$v = DistancejobPeer::doSelect($criteria, $con);

		return !empty($v) ? $v[0] : null;
	}
