	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collUserLoginss) {
				foreach ((array) $this->collUserLoginss as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collGeneralComments) {
				foreach ((array) $this->collGeneralComments as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collQueryComments) {
				foreach ((array) $this->collQueryComments as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collUserLoginss = null;
		$this->collGeneralComments = null;
		$this->collQueryComments = null;
	}

