	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseSmintUser:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseSmintUser::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
